  return (
    <form className="task-form" onSubmit={handleSubmit}>
      <input
        type="text"
        name="add"
        placeholder="Type to add new tasks"
        value={text}
        onChange={e => setText(e.target.value)}
      />

      <button type="submit">Add Task</button>
    </form>
  );
};
